  `gamer_count` int(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `room_id` (`room_id`,`pos`) USING BTREE,
  KEY `room_id_index` (`room_id`) USING BTREE
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

